import axios from 'axios'
import React, { useEffect ,useState} from 'react'
import "./conversation.css"
export default function Conversation({conversation, currentUser}) {
  const [user, setUser] = useState()
  const [number, setNumber] = useState()
  useEffect(()=>{
    const friendId =conversation.members.find(m=>m!==currentUser._id)
    const getUser  = async ()=>{
      try{
        // console.log("saif");
        const res = await axios.get("http://localhost:4001/api/v1/user/"+friendId, {withCredentials : "same-orgin"})
        console.log(res.data.user);
        setUser(res.data.user)
      }catch(err){
        console.log(err);
      }
    }
    getUser()
    // console.log(user);
  }, [])


  return (
    <div className='conversation'>
        <img className='conversationImage' src="http://res.cloudinary.com/ddvtgfqgv/image/upload/v1672331530/user-image/wuyafjuasl9czeikqliz.png" />
        <span className='conversationName'>{number}</span>
        <button onClick={() => setNumber(1)}>okay</button>
    </div>
  )
}
