import React, {  useEffect, useState } from 'react'
import "./messenger.css"
import Conversation from '../../components/conversations/conversation'
import Message from '../../components/message/Message'
import ChatOnline from '../../components/chatOnline/ChatOnline'
import axios from 'axios'
import { useCookies } from 'react-cookie'
// import TopBar from "../../components/topbar/Topbar"
export default function Messenger() {
  // const [cookies, setCookie] = useCookies(['access_token', 'refresh_token'])
  const[conversation , setConversation] = useState([])
  useEffect(()=>{
    const getConversation = async ()=>{
      try{
      const res = await axios.get("http://localhost:4001/api/v1/getChatRoom",{withCredentials: "same-origin"})
      setConversation(res.data)
      console.log(res.data);
      console.log(res.user);
    }catch(err){
      console.log(err);
    }
    }
    getConversation()
  }, [])

  return (
    <>
    <div className='messenger'>
      <div className='chatMenu'>
        <div className="chatMenuWrapper">

          <input placeholder='Search for Friend' className='chatMenuInput' />
          {conversation.map((c)=>{

          <Conversation conversation = {c} />
          })}
          {/* <Conversation/> */}
        </div>
      </div>

      <div className='chatBox'>
        <div className="chatBoxWrapper">
          <div className="chatBoxTop">
            <Message/>
            <Message own={true}/>
            <Message/>
            <Message own={true}/>
            <Message/>
            <Message own={true}/>
            <Message/>
            <Message own={true}/>
            <Message/>
            <Message own={true}/>
            <Message/>
            <Message own={true}/>


          </div>
          <div className="chatBoxBottom">
            <textarea className='chatMessageInput' placeholder='Write a Message' ></textarea>
            <button className='chatSubmitButton'>Send</button>
          </div>
        </div>
      </div>
      <div className='chatOnline'>
        <div className="chatOnlineWrapper">
          <ChatOnline/>
          <ChatOnline/>
          <ChatOnline/>

        </div>
      </div>
    </div>

    </>
  )
}
